mapper.select:
    select 
    
    from ct_company_user where 1 = 1
	
		and user_name = #{userName} 
	
	
		and email = #{email}
	
	
		and id != #{id}

SELECT u.*,org.org_name as company,
org.serve_charge as serveCharge,org.serve_charge_percent as serveChargePercent
from ct_company_user u
left join ct_company_organization org on u.customer_id = org.customer_id
where
1=1

  and u.user_name = #{userName}


  and u.user_password = #{userPassword}


  and u.ydj_token = #{ydjToken}


  and u.token = #{token}

select u.id,u.user_name,u.real_name,u.owner,u.create_time,u.last_login_time,u.status,r.role_name,org.org_name as company, org.serve_charge as serveCharge,org.serve_charge_percent as serveChargePercent from ct_company_user u left join ct_company_organization org on u.customer_id = org.customer_id left join ct_company_role r on u.role_id = r.role_id where u.create_id = #{createId}
and org.org_name like '%${keywords}%'
group by u.id order by u.create_time desc

select u.*,org.org_name as company,org.serve_charge as serveCharge,org.serve_charge_percent as serveChargePercent from ct_company_user u
left join ct_company_organization org on u.customer_id = org.customer_id 
where u.id = #{id}
